        methods:{
            export_action:function(){
                this.$nextTick(function(){
                    var export_html_dom = this.$refs.export_html;
                    var imgs = export_html_dom.getElementsByTagName("img");
                    for(var i = 0;i<imgs.length;i++){
                        var img = imgs[i];
                        img.style.maxWidth = "400px !important";
                        img.style.width = "400px !important";
                        var width = img.width;
                        if(width>400){
                            img.height = img.height*400/width;
                            img.width = 400;
                        }
                    }
                    // console.info(imgs);
                    var export_html = export_html_dom.innerHTML;
                    // console.info(export_html);
                    var blob = new Blob([export_html], { type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document;charset=utf-8'}); //application/vnd.openxmlformats-officedocument.wordprocessingml.document这里表示doc类型
                    var downloadElement = document.createElement('a');
                    var href = window.URL.createObjectURL(blob); //创建下载的链接
                    downloadElement.href = href;
                    downloadElement.download ='ques.doc'; //下载后文件名
                    document.body.appendChild(downloadElement);
                    downloadElement.click(); //点击下载
                    document.body.removeChild(downloadElement); //下载完成移除元素
                    window.URL.revokeObjectURL(href); //释放掉blob对象
                });
            }
        }
    }
</script>

